from flask import Flask, url_forimport dominatefrom dominate.tags import *app = Flask(__name__)@app.route('/')def home():    doc = dominate.document(title='Home Page')    with doc.head:        link(rel='stylesheet', href='https://fonts.googleapis.com/css?family=Roboto')    # Generate the correct path for the static logo without the leading slash    logo_url = 'static/logo.png'  # Change this line to remove the leading slash    with doc:        with body(style="background-color: rgb(229, 204, 255);"):            with div(style="text-align: center; margin-top: 50px;"):                h1('Welcome to Na Laochra Aeracha Home Page')                p('Cork LGBTQ+ Inclusive GAA (football) Club')                img(src=logo_url, width="400")  # Use the modified URL    return str(doc)def save_static_html(filename='index.html'):    # Generate HTML content from the home route    with app.test_request_context('/'):        html_content = home()        # Save the HTML content to a file        with open(filename, 'w') as f:            f.write(html_content)if __name__ == '__main__':    save_static_html()  # Generate and save the HTML file    app.run(debug=True)  # Start the Flask app