from flask import Flask, url_forimport dominatefrom dominate.tags import *app = Flask(__name__)@app.route('/')def home():    doc = dominate.document(title='Home Page')    with doc.head:        link(rel='stylesheet', href='https://fonts.googleapis.com/css?family=Roboto')    # Generate the correct path for the static logo without the leading slash    logo_url = 'static/logo.png'  # Change this line to remove the leading slash    with doc:        doc.add(render_navbar())        with body(style="background-color: rgb(229, 204, 255);"):            with div(style="text-align: center; margin-top: 50px;"):                h1('Welcome to Na Laochra Aeracha Home Page')                p('Cork LGBTQ+ Inclusive GAA (football) Club')                img(src=logo_url, width="400")  # Use the modified URL    return str(doc)        def render_navbar():    nav_bar = nav()    ul_list = ul(style="list-style-type: none; margin: 0; padding: 0; overflow: hidden; background-color: #138;")        # Home Link    ul_list += li(a('Home', href='index.html', style="display: block; color: white; text-align: center; padding: 14px 16px; text-decoration: none;"),                   style="float: left;")        # Galary Link    ul_list += li(a('Galary', href='galary.html', style="display: block; color: white; text-align: center; padding: 14px 16px; text-decoration: none;"),                   style="float: left;")        # About Link    ul_list += li(a('About', href='about.html', style="display: block; color: white; text-align: center; padding: 14px 16px; text-decoration: none;"),                   style="float: left;")        # Contact Link    ul_list += li(a('Contact', href='contact.html', style="display: block; color: white; text-align: center; padding: 14px 16px; text-decoration: none;"),                   style="float: left;")        nav_bar.add(ul_list)    return nav_bar            # New Gallery page route@app.route('/about')def about():    doc = dominate.document(title='About')    with doc.head:        link(rel='stylesheet', href='https://fonts.googleapis.com/css?family=Roboto')    with doc:        doc.add(render_navbar())        with body(style="background-color: rgb(229, 204, 255);"):            with div(style="text-align: center; margin-top: 50px;"):                h1('About')                p('The Na Laochra Aeracha is the first LGBTQ+ Inclusve Gaelic Footabll Club. Training is every Wednesday 18:30 to 19:30 and Sundays 10:30 to 11:30.')                        return str(doc)    def save_static_html(filename='index.html'):    # Generate HTML content from the home route    with app.test_request_context('/'):        html_content = home()        # Save the HTML content to a file        with open(filename, 'w') as f:            f.write(html_content)def save_static_html_about(filename='about.html'):    # Generate HTML content from the home route    with app.test_request_context('/about'):        html_content = about()        # Save the HTML content to a file        with open(filename, 'w') as f:            f.write(html_content)if __name__ == '__main__':    save_static_html()  # Generate and save the HTML file    save_static_html_about()    app.run(debug=True)  # Start the Flask app